import React from "react";
import Layout from "../../components/Layout";

const Profil = () => {
  const renderAddwebSiteModal = () => {
    return (
      <Modal
        show={show}
        handleClose={handleClose}
        modalTitle={"Add New webSite"}
        onSubmit={submitwebSiteForm}
      >
        <Input
          label="Name"
          value={name}
          placeholder={`website Name`}
          onChange={(e) => setName(e.target.value)}
        />
        <Input
          label="number of visitors"
          value={quantity}
          placeholder={`number of visitors`}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <Input
          label="Link"
          value={"www.linkedin.com"}
          placeholder={`website link`}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <Input
          label="Price"
          value={price}
          placeholder={`Price`}
          onChange={(e) => setPrice(e.target.value)}
        />
        <Input
          label="Description"
          value={description}
          placeholder={`Description`}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select
          className="form-control"
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}
        >
          <option>select category</option>
          {createCategoryList(category.categories).map((option) => (
            <option key={option.value} value={option.value}>
              {option.name}
            </option>
          ))}
        </select>
        {webSitePictures.length > 0
          ? webSitePictures.map((pic, index) => (
              <div key={index}>{pic.name}</div>
            ))
          : null}
        <input
          type="file"
          name="webSitePicture"
          onChange={handlewebSitePictures}
        />
      </Modal>
    );
  };
  return (
    <Layout>
      <Container>
        <Row>
          <Col md={12}>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <h3>Website</h3>
              <button onClick={handleShow}>Add</button>
            </div>
          </Col>
        </Row>
        <Row>
          <Col>{renderwebSites()}</Col>
        </Row>
      </Container>
      {renderAddwebSiteModal()}
    </Layout>
  );
};

export default Profil;
