import React, { useState } from "react";
import Layout from "../../components/Layout";
import { Container, Row, Col, Table } from "react-bootstrap";
import Input from "../../components/UI/Input";
import Modal from "../../components/UI/Modal";
import { useSelector, useDispatch } from "react-redux";
import { addProduct } from "../../actions/product.action";
import { profilePicture } from "./consPicture";
import "./style.css";
const Profil = () => {
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [webSitePictures, setwebSitePictures] = useState([]);
  const [show, setShow] = useState(false);
  const [webSiteDetailModal, setWebsiteDetailModal] = useState(false);
  const [websiteDetails, setwebsiteDetails] = useState(null);
  const category = useSelector((state) => state.category);
  const webSite = useSelector((state) => state.product);
  const dispatch = useDispatch();
  const handleClose = () => {
    setShow(false);
  };
  const handleShow = () => setShow(true);
  const submitwebSiteForm = () => {
    const form = new FormData();
    form.append("name", name);
    form.append("quantity", quantity);
    form.append("price", price);
    form.append("description", description);
    form.append("category", categoryId);
    for (let pic of webSitePictures) {
      form.append("webSitePicture", pic);
    }
    dispatch(addProduct(form)).then(() => setShow(false));
  };

  const createCategoryList = (categories, options = []) => {
    for (let category of categories) {
      options.push({ value: category._id, name: category.name });
      if (category.children.length > 0) {
        createCategoryList(category.children, options);
      }
    }

    return options;
  };
  const handlewebSitePictures = (e) => {
    setwebSitePictures([...webSitePictures, e.target.files[0]]);
  };
  const renderAddwebSiteModal = () => {
    return (
      <Modal
        className="modalWebsite"
        show={show}
        handleClose={handleClose}
        modalTitle={"Add New webSite"}
        onSubmit={submitwebSiteForm}
      >
        <Input
          label="Name"
          value={name}
          placeholder={`website Name`}
          onChange={(e) => setName(e.target.value)}
        />
        <Input
          label="number of visitors"
          value={quantity}
          placeholder={`number of visitors`}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <Input
          label="Link"
          value={"www.linkedin.com"}
          placeholder={`website link`}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <Input
          label="Price"
          value={price}
          placeholder={`Price`}
          onChange={(e) => setPrice(e.target.value)}
        />
        <Input
          label="Description"
          value={description}
          placeholder={`Description`}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select
          className="form-control"
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}
        >
          <option>select category</option>
          {createCategoryList(category.categories).map((option) => (
            <option key={option.value} value={option.value}>
              {option.name}
            </option>
          ))}
        </select>
        {webSitePictures.length > 0
          ? webSitePictures.map((pic, index) => (
              <div key={index}>{pic.name}</div>
            ))
          : null}
        <input
          type="file"
          name="webSitePicture"
          onChange={handlewebSitePictures}
        />
      </Modal>
    );
  };

  return (
    <Layout>
      <Container>
        <Row>
          <Col md="6">
            <img
              src={profilePicture}
              alt="profile"
              style={{ height: "180px", width: "180px" }}
            />
          </Col>
          <Col md="6">
            <Col md="6">
              <label className="key">firstName</label>
              <p className="value">{userDetails.firstName ?? ""}</p>
            </Col>
            <Col md="6">
              <label className="key">lastName</label>
              <p className="value">{userDetails.lastName ?? ""}</p>
            </Col>
            <Col md="6">
              <label className="key">email</label>
              <p className="value">{userDetails.email ?? ""}</p>
            </Col>
            <Col md="6">
              <label className="key">Country</label>
              <p className="value">{userDetails.country ?? "tunis"}</p>
            </Col>
            <Col md="6">
              <label className="key">Sexe</label>
              <p className="value">{userDetails.sexe ?? "F"}</p>
            </Col>
            <Col md="6">
              <label className="key">Statut </label>
              <p className="value">{userDetails.statut ?? "blogger"}</p>
            </Col>
            <Col md="6">
              <label className="key">contact number </label>
              <p className="value">{userDetails.contactNumber ?? "12345"}</p>
            </Col>
            <Col md="6">
              <label className="key">number of site created</label>
              <p className="value">{userDetails.contactNumber ?? "4"}</p>
              <Link to="/:id/sites">
                sites created by {userDetails.firstName ?? ""}
              </Link>
            </Col>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <h3>Website</h3>
              <button onClick={handleShow}>Add</button>
            </div>
          </Col>
        </Row>
        <Row></Row>
      </Container>
      {renderAddwebSiteModal()}
    </Layout>
  );
};

export default Profil;
