import React, { useEffect, useState } from "react";
import "./style.css";

import goldenStar from "../../images/logo/golden-star.png";
import { IoIosArrowDown, IoIosCart, IoIosSearch } from "react-icons/io";
import {
  Modal,
  MaterialInput,
  MaterialButton,
  DropdownMenu,
  MaterialSelect,
} from "../../components/MaterialUI";
import { useDispatch, useSelector } from "react-redux";
import { login, signout, getCartItems, signup as _signup } from "../../actions";
import Cart from "../../components/UI/Cart";
import { NavLink, Link } from "react-router-dom";
import { profilePicture } from "../../components/Header/consPicture";
import Layout from "../../components/Layout";
import { Button, Col, Container, Row } from "react-bootstrap";
/**
 * @author
 * @function Header
 **/

const Website = () => {
  const [loginModal, setLoginModal] = useState(true);
  const [websiteModal, setWebsiteModal] = useState(false);
  const [signup, setSignup] = useState(true);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [statut, setStatut] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [sexe, setSexe] = useState("");
  const [country, setCountry] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [sexeList, setSexeList] = useState("");
  const auth = useSelector((state) => state.auth);
  const dispatch = useDispatch();

  // state cart value
  const cart = useSelector((state) => state.cart);

  const userSignup = () => {
    const user = {
      firstName,
      lastName,
      email,
      password,
      sexe,
      contactNumber,
      country,
      statut,
    };
    if (
      firstName === "" ||
      lastName === "" ||
      email === "" ||
      password === "" ||
      sexe === "" ||
      country === "" ||
      contactNumber === "" ||
      statut === ""
    ) {
      return;
    }

    dispatch(_signup(user));
  };

  const userLogin = () => {
    if (signup) {
      userSignup();
    } else {
      dispatch(login({ email, password }));
    }
  };

  const logout = () => {
    dispatch(signout());
  };

  useEffect(() => {
    if (auth.authenticate) {
      setLoginModal(false);
    }
  }, [auth.authenticate]);

  const renderNonLoggedInMenu = () => {
    return (
      <DropdownMenu
        menu={
          <a
            className="loginButton"
            onClick={() => {
              setSignup(false);
              setLoginModal(true);
            }}
          >
            Login
          </a>
        }
        firstMenu={
          <div className="firstmenu">
            <span>New Customer?</span>
            <a
              onClick={() => {
                setLoginModal(true);
                setSignup(true);
              }}
              style={{ color: "#2874f0" }}
            >
              Sign Up
            </a>
          </div>
        }
      />
    );
  };

  return (
    <Layout className="header">
      <div className="headerwebsite">
        <h3 className="titleWebsite">Websites</h3>
        <button
          className="buttonwebsiteadd"
          onClick={() => setWebsiteModal(!websiteModal)}
        >
          Add
        </button>
      </div>
      <Modal visible={websiteModal} onClose={() => setWebsiteModal(false)}>
        <div className="authContainer">
          <div className="row">
            <div className="rightspace">
              <div className="loginInputContainer">
                <MaterialInput
                  type="text"
                  label="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="url site"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                />
                <MaterialInput
                  type="Number"
                  label="publicationPrice"
                  value={publicationPrice}
                  onChange={(e) => setPublicationPrice(e.target.value)}
                />

                <MaterialInput
                  type="devise"
                  label="devise"
                  value={devise}
                  onChange={(e) => setDevise(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="country"
                  value={country}
                  onChange={(e) => setCountry(e.target.value)}
                />

                <br />
                <>
                  <select
                    style={{ width: "100%", border: "1px solid white" }}
                    className="form-select"
                    type="select "
                    label="sexe"
                    placeholder="sexe"
                    value={sexe}
                    onChange={(e) => setSexe(e.target.value)}
                  >
                    <option value="--Please choose an option--">
                      --Please choose an option--
                    </option>
                    <option value="male">male</option>
                    <option value="female">female</option>
                    <option value="other">other</option>
                  </select>
                </>

                <>
                  <select
                    style={{
                      width: "100%",
                      border: "1px solid white",
                      marginTop: "20px",
                    }}
                    className="form-select mt-5"
                    type="select "
                    label="status"
                    placeholder="status"
                    value={statut}
                    onChange={(e) => setStatut(e.target.value)}
                  >
                    <option value="--Please choose an option--">
                      --Please choose an option--
                    </option>

                    <option value="bloggeur">bloggeur</option>
                    <option value="company">company</option>
                    <option value="agency">agency</option>
                    <option value="site owner">site owner</option>
                  </select>
                </>
                <MaterialButton
                  title={"submit"}
                  bgColor="#fb641b"
                  textColor="#ffffff"
                  style={{
                    margin: "40px 0 20px 0",
                  }}
                  onClick={userLogin}
                />
              </div>
            </div>
          </div>
        </div>
      </Modal>
    </Layout>
  );
};

export default Website;
