import React, { useEffect, useState } from "react";
import "./style.css";

import goldenStar from "../../images/logo/golden-star.png";
import { IoIosArrowDown, IoIosCart, IoIosSearch } from "react-icons/io";
import {
  Modal,
  MaterialInput,
  MaterialButton,
  DropdownMenu,
  MaterialSelect,
} from "../../components/MaterialUI";
import { useDispatch, useSelector } from "react-redux";
import { login, signout, getCartItems, signup as _signup } from "../../actions";
import Cart from "../../components/UI/Cart";
import { NavLink, Link } from "react-router-dom";
import { profilePicture } from "../../components/Header/consPicture";
import Layout from "../../components/Layout";
import { Button, Col, Container, Row } from "react-bootstrap";
/**
 * @author
 * @function Header
 **/

const Website = () => {
  const [websiteModal, setWebsiteModal] = useState(false);
  const [name, setName] = useState("");
  const [url, setUrl] = useState("");
  const [description, setDescription] = useState("");
  const [publicationPrice, setPublicationPrice] = useState("");
  const [devise, setDevice] = useState("");
  const [typeSite, setTypeSite] = useState("");
  const [productPictures, setProductPictures] = useState([]);
  const [visitorsPerMonth, setVisitorsPerMounth] = useState("");

  const [error, setError] = useState("");
  const [sexeList, setSexeList] = useState("");
  const auth = useSelector((state) => state.auth);
  const dispatch = useDispatch();

  // state cart value
  const cart = useSelector((state) => state.cart);

  const userSignup = () => {
    const user = {
      firstName,
      lastName,
      email,
      password,
      sexe,
      contactNumber,
      country,
      statut,
    };
    if (
      firstName === "" ||
      lastName === "" ||
      email === "" ||
      password === "" ||
      sexe === "" ||
      country === "" ||
      contactNumber === "" ||
      statut === ""
    ) {
      return;
    }

    dispatch(_signup(user));
  };

  return (
    <Layout className="header">
      <div className="headerwebsite">
        <h3 className="titleWebsite">Websites</h3>
        <button
          className="buttonwebsiteadd"
          onClick={() => setWebsiteModal(!websiteModal)}
        >
          Add
        </button>
      </div>
      <Modal visible={websiteModal} onClose={() => setWebsiteModal(false)}>
        <div className="authContainer">
          <div className="row">
            <div className="rightspace">
              <div className="loginInputContainer">
                <MaterialInput
                  type="text"
                  label="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="url site"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                />
                <MaterialInput
                  type="Number"
                  label="publicationPrice"
                  value={publicationPrice}
                  onChange={(e) => setPublicationPrice(e.target.value)}
                />
                <MaterialInput
                  type="Number"
                  label="visitors Per Month"
                  value={visitorsPerMonth}
                  onChange={(e) => setVisitorsPerMounth(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="typeSite"
                  value={typeSite}
                  onChange={(e) => setTypeSite(e.target.value)}
                />

                <br />
                <>
                  <select
                    style={{ width: "100%", border: "1px solid white" }}
                    className="form-select"
                    type="select "
                    label="type Site"
                    placeholder="type Site"
                    value={typeSite}
                    onChange={(e) => setTypeSite(e.target.value)}
                  >
                    <option value="--Please choose an option--">
                      --Please choose an option--
                    </option>
                    <option value="site">site</option>
                    <option value="blog">blog</option>
                    <option value="Magazine">Magazine</option>
                  </select>
                </>

                <>
                  <select
                    style={{
                      width: "100%",
                      border: "1px solid white",
                      marginTop: "20px",
                    }}
                    className="form-select mt-5"
                    type="select "
                    label="device"
                    placeholder="device"
                    value={devise}
                    onChange={(e) => setDevice(e.target.value)}
                  >
                    <option value="--Please choose an option--">
                      --Please choose an option--
                    </option>

                    <option value="bloggeur">bloggeur</option>
                    <option value="company">company</option>
                    <option value="agency">agency</option>
                    <option value="site owner">site owner</option>
                  </select>
                </>
                <MaterialButton
                  title={"submit"}
                  bgColor="#fb641b"
                  textColor="#ffffff"
                  style={{
                    margin: "40px 0 20px 0",
                  }}
                  onClick={userSignup}
                />
              </div>
            </div>
          </div>
        </div>
      </Modal>
    </Layout>
  );
};

export default Website;
