import React, { useEffect, useState } from "react";
import "./style.css";

import goldenStar from "../../images/logo/golden-star.png";
import { IoIosArrowDown, IoIosCart, IoIosSearch } from "react-icons/io";
import {
  Modal,
  MaterialInput,
  MaterialButton,
  DropdownMenu,
  MaterialSelect,
} from "../../components/MaterialUI";
import { useDispatch, useSelector } from "react-redux";
import { login, signout, getCartItems, signup as _signup } from "../../actions";
import Cart from "../../components/UI/Cart";
import { NavLink, Link } from "react-router-dom";
import { profilePicture } from "../../components/Header/consPicture";
import Layout from "../../components/Layout";
import { Button, Col, Container, Row } from "react-bootstrap";
/**
 * @author
 * @function Header
 **/

const Website = () => {
  const [websiteModal, setWebsiteModal] = useState(false);
  const [name, setName] = useState("");
  const [url, setUrl] = useState("");
  const [description, setDescription] = useState("");
  const [publicationPrice, setPublicationPrice] = useState("");
  const [devise, setDevice] = useState("");
  const [typeSite, setTypeSite] = useState("");
  const [productPictures, setProductPictures] = useState([]);
  const [visitorsPerMonth, setVisitorsPerMounth] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [error, setError] = useState("");
  const [sexeList, setSexeList] = useState("");
  const auth = useSelector((state) => state.auth);
  const category = useSelector((state) => state.category);
  const dispatch = useDispatch();

  // state cart value
  const cart = useSelector((state) => state.cart);
  const createCategoryList = (categories, options = []) => {
    for (let category of categories) {
      options.push({ value: category._id, name: category.name });
      if (category.children.length > 0) {
        createCategoryList(category.children, options);
      }
    }
    return options;
  };
  const userSignup = () => {
    for (let pic of productPictures) {
      form.append("productPictures", pic);
    }
    const user = {
      _id: auth.user._d,
      category,
      name,
      url,
      description,
      publicationPrice,
      devise,
      typeSite,
      productPictures,
      visitorsPerMonth,
    };
    if (
      name === "" ||
      url === "" ||
      description === "" ||
      devise === "" ||
      typeSite === "" ||
      productPictures === []
    ) {
      return;
    }

    dispatch(_signup(user));
  };

  return (
    <Layout className="header">
      <div className="headerwebsite">
        <h3 className="titleWebsite">Websites</h3>
        <button
          className="buttonwebsiteadd"
          onClick={() => setWebsiteModal(!websiteModal)}
        >
          Add
        </button>
      </div>
      <Modal visible={websiteModal} onClose={() => setWebsiteModal(false)}>
        <div className="authContainer">
          <div className="row">
            <div className="rightspace">
              <div className="loginInputContainer">
                <MaterialInput
                  type="text"
                  label="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="url site"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                />

                <MaterialInput
                  type="text"
                  label="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                />
                <MaterialInput
                  type="Number"
                  label="publication Price"
                  value={publicationPrice}
                  onChange={(e) => setPublicationPrice(e.target.value)}
                />
                <MaterialInput
                  type="Number"
                  label="Number of visitors Per Month"
                  value={visitorsPerMonth}
                  onChange={(e) => setVisitorsPerMounth(e.target.value)}
                />

                <br />
                <>
                  <select
                    style={{ width: "100%", border: "1px solid white" }}
                    className="form-select"
                    type="select "
                    label="type Site"
                    placeholder="type Site"
                    value={devise}
                    onChange={(e) => setDevice(e.target.value)}
                  >
                    <option value="--Please choose an option--">
                      --Please choose an option--
                    </option>
                    <option value="Usd">Usd</option>
                    <option value="Euro">Euro</option>
                    <option value="Tnd">Tnd</option>
                  </select>
                </>

                <>
                  <select
                    style={{
                      width: "100%",
                      border: "1px solid white",
                      marginTop: "20px",
                    }}
                    className="form-select mt-5"
                    type="select "
                    label="device"
                    placeholder="device"
                    value={typeSite}
                    onChange={(e) => setTypeSite(e.target.value)}
                  >
                    <option value="--Please choose an option--">
                      --Please choose an option--
                    </option>

                    <option value="bloggeur">bloggeur</option>
                    <option value="company">company</option>
                    <option value="agency">agency</option>
                  </select>
                </>
                <MaterialButton
                  title={"submit"}
                  bgColor="#fb641b"
                  textColor="#ffffff"
                  style={{
                    margin: "40px 0 20px 0",
                  }}
                  onClick={userSignup}
                />
              </div>
            </div>
          </div>
        </div>
      </Modal>
    </Layout>
  );
};

export default Website;
