import React, { useEffect, useState } from "react";
import Layout from "../../components/Layout";
import { Container, Row, Col, Table } from "react-bootstrap";
import Input from "../../components/UI/Input";
import Modal from "../../components/UI/Modal";
import { useSelector, useDispatch } from "react-redux";
import { addProduct } from "../../actions/product.action";
import { profilePicture } from "../../components/Header/consPicture";
import "./style.css";
import { Redirect } from "react-router-dom";

const Profil = () => {
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [webSitePictures, setwebSitePictures] = useState([]);
  const [show, setShow] = useState(false);
  const [webSiteDetailModal, setWebsiteDetailModal] = useState(false);
  const [websiteDetails, setwebsiteDetails] = useState(null);
  const category = useSelector((state) => state.category);
  const webSite = useSelector((state) => state.product);
  const auth = useSelector((state) => state.auth);

  const dispatch = useDispatch();

  const handleClose = () => {
    setShow(false);
  };

  const handleShow = () => setShow(true);
  const submitwebSiteForm = () => {
    const form = new FormData();
    form.append("name", name);
    form.append("quantity", quantity);
    form.append("price", price);
    form.append("description", description);
    form.append("category", categoryId);
    for (let pic of webSitePictures) {
      form.append("webSitePicture", pic);
    }
    dispatch(addProduct(form)).then(() => setShow(false));
  };
  console.log("auth", auth);
  return (
    <Layout>
      <div class="card-container">
        <span class="pro">{auth.user.role}</span>
        <img class="round" src={profilePicture} alt="user" />
        <h3>{auth.user.fullName}</h3>
        <h6>{auth.user.country}</h6>
        <p>
          User interface designer and <br /> front-end developer
        </p>
        <div class="buttons">
          <button class="primary">Message</button>
          <button class="primary ghost">Following</button>
        </div>
        <div class="skills">
          <h6>Skills</h6>
          <ul>
            <li>UI / UX</li>
            <li>Front End Development</li>
            <li>HTML</li>
            <li>CSS</li>
            <li>JavaScript</li>
            <li>React</li>
            <li>Node</li>
          </ul>
        </div>
      </div>

      <footer>
        <p>
          Created with <i class="fa fa-heart"></i> by
          <a target="_blank" href="https://florin-pop.com">
            Florin Pop
          </a>
          - Read how I created this
          <a
            target="_blank"
            href="https://florin-pop.com/blog/2019/04/profile-card-design"
          >
            here
          </a>
          - Design made by
          <a
            target="_blank"
            href="https://dribbble.com/shots/6276930-Profile-Card-UI-Design"
          >
            Ildiesign
          </a>
        </p>
      </footer>
    </Layout>
  );
};

export default Profil;
