import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductsBySlug } from "../../../actions";
import { Link } from "react-router-dom";
import Card from "../../../components/UI/Card";
import { MaterialButton } from "../../../components/MaterialUI";
import Rating from "../../../components/UI/Rating";
import Price from "../../../components/UI/Price";
import Layout from "../../../components/Layout";
import { Button, Col, Container, Row, Table } from "react-bootstrap";
import parse from "html-react-parser";
import { profilePicture } from "../../../components/Header/consPicture";
/**
 * @author
 * @function ProductStore
 **/
import { getProductsByUserId } from "../../../actions/product.action";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
const ProductStore = (props) => {
  const product = useSelector((state) => state.product);

  const priceRange = product.priceRange;
  const dispatch = useDispatch();
  const images = [
    "https://images.pexels.com/photos/347141/pexels-photo-347141.jpeg?auto=compress&cs=tinysrgb&w=600",
    "https://images.pexels.com/photos/3155666/pexels-photo-3155666.jpeg?auto=compress&cs=tinysrgb&w=600",
    "https://images.pexels.com/photos/1591373/pexels-photo-1591373.jpeg?auto=compress&cs=tinysrgb&w=600",
  ];
  useEffect(() => {
    const { match } = props;
    dispatch(getProductsBySlug(match.params.slug));
  }, []);
  console.log("product.products", product.products);
  const carousel = () => {
    return (
      <Carousel renderThumbs={() => {}}>
        {images &&
          images.map((banner, index) => (
            <a
              key={index}
              style={{ display: "block" }}
              href={banner.navigateTo}
            >
              <img src={banner} alt="" />
            </a>
          ))}
      </Carousel>
    );
  };
  const renderwebSites = () => {
    return (
      <Container className="cardItems">
        {product.products && product
          ? product.products.map((site) => {
              return (
                <>
                  <Card>
                    <li>
                      <a class="card">
                        {carousel()}
                        <div class="card__overlay">
                          <div class="card__header">
                            <svg
                              class="card__arc"
                              xmlns="http://www.w3.org/2000/svg"
                            >
                              <path />
                            </svg>
                            <img
                              class="card__thumb"
                              src={profilePicture}
                              alt=""
                            />
                            <div class="card__header-text">
                              <h3 class="card__title">{site.name}</h3>
                              <span class="card__status">{site.createdAt}</span>
                            </div>
                          </div>
                          <p class="card__description">
                            URL:<div>{site.url}</div>
                          </p>
                          <p class="card__description">
                            type of site :<div>{site.typeSite}</div>
                          </p>
                          <p class="card__description">
                            visitors per mounth:
                            <div>{site.visitorsPerMonth}</div>
                          </p>
                          <p class="card__description">
                            PRICE OF PUBLIACTION:
                            <div>{site.publicationPrice}</div>
                          </p>
                          <p class="card__description">
                            DEVICE:<div>{site.devise}</div>
                          </p>

                          <p class="card__description">
                            description:<div>{parse(site.description)}</div>
                          </p>
                        </div>
                      </a>
                    </li>
                  </Card>
                </>
              );
            })
          : null}
      </Container>
    );
  };

  return (
    <Layout>
      <>
        <Container>
          <Row>
            <Col>{renderwebSites()}</Col>
          </Row>
        </Container>
      </>
    </Layout>
  );
};

export default ProductStore;
