import React, { useEffect, useState } from "react";
import Layout from "../../components/Layout";
import { Container, Row, Col, Table } from "react-bootstrap";
import Input from "../../components/UI/Input";
import Modal from "../../components/UI/Modal";
import { useSelector, useDispatch } from "react-redux";
import { addProduct } from "../../actions/product.action";
import { profilePicture } from "../../components/Header/consPicture";
import "./style.css";
import { Redirect } from "react-router-dom";

const Profil = () => {
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [webSitePictures, setwebSitePictures] = useState([]);
  const [show, setShow] = useState(false);
  const [webSiteDetailModal, setWebsiteDetailModal] = useState(false);
  const [websiteDetails, setwebsiteDetails] = useState(null);
  const category = useSelector((state) => state.category);
  const webSite = useSelector((state) => state.product);
  const auth = useSelector((state) => state.auth);
  console.log("auth", auth);
  const dispatch = useDispatch();

  const handleClose = () => {
    setShow(false);
  };
  const handleShow = () => setShow(true);
  const submitwebSiteForm = () => {
    const form = new FormData();
    form.append("name", name);
    form.append("quantity", quantity);
    form.append("price", price);
    form.append("description", description);
    form.append("category", categoryId);
    for (let pic of webSitePictures) {
      form.append("webSitePicture", pic);
    }
    dispatch(addProduct(form)).then(() => setShow(false));
  };

  const createCategoryList = (categories, options = []) => {
    for (let category of categories) {
      options.push({ value: category._id, name: category.name });
      if (category.children.length > 0) {
        createCategoryList(category.children, options);
      }
    }
    return options;
  };

  const handlewebSitePictures = (e) => {
    setwebSitePictures([...webSitePictures, e.target.files[0]]);
  };
  const renderAddwebSiteModal = () => {
    return (
      <Modal
        className="modalWebsite"
        show={show}
        handleClose={handleClose}
        modalTitle={"Add New webSite"}
        onSubmit={submitwebSiteForm}
      >
        <Input
          label="Name"
          value={name}
          placeholder={`website Name`}
          onChange={(e) => setName(e.target.value)}
        />
        <Input
          label="number of visitors"
          value={quantity}
          placeholder={`number of visitors`}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <Input
          label="Link"
          value={"www.linkedin.com"}
          placeholder={`website link`}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <Input
          label="Price"
          value={price}
          placeholder={`Price`}
          onChange={(e) => setPrice(e.target.value)}
        />
        <Input
          label="Description"
          value={description}
          placeholder={`Description`}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select
          className="form-control"
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}
        >
          <option>select category</option>
          {createCategoryList(category.categories).map((option) => (
            <option key={option.value} value={option.value}>
              {option.name}
            </option>
          ))}
        </select>
        {webSitePictures.length > 0
          ? webSitePictures.map((pic, index) => (
              <div key={index}>{pic.name}</div>
            ))
          : null}
        <input
          type="file"
          name="webSitePicture"
          onChange={handlewebSitePictures}
        />
      </Modal>
    );
  };

  return (
    <Layout>
      <Container className="profile_container">
        <Row className="profileRow">
          <Col md="6" className="colprofile">
            <img
              src={profilePicture}
              alt="profile"
              className="profilePricture"
              style={{ height: "180px", width: "180px" }}
            />
          </Col>
          <Col md="6" className="userInfo">
            <Col md="6">
              <label className="key">firstName</label>
              <p className="value">{auth.user.firstName ?? ""}</p>
            </Col>
            <Col md="6">
              <label className="key">lastName</label>
              <p className="value">{auth.user.lastName ?? ""}</p>
            </Col>
            <Col md="6">
              <label className="key">email</label>
              <p className="value">{auth.user.email ?? ""}</p>
            </Col>
            <Col md="6">
              <label className="key">Country</label>
              <p className="value">{auth.user.country ?? "tunis"}</p>
            </Col>
            <Col md="6">
              <label className="key">Sexe</label>
              <p className="value">{auth.user.sexe ?? "F"}</p>
            </Col>
            <Col md="6">
              <label className="key">Statut </label>
              <p className="value">{auth.user.statut ?? "blogger"}</p>
            </Col>
            <Col md="6">
              <label className="key">contact number </label>
              <p className="value">{auth.user.contactNumber ?? "4"}</p>
            </Col>
            <Col md="6">
              <label className="key">number of site created</label>
              <p className="value">{"4"}</p>
            </Col>
          </Col>
        </Row>
      </Container>
      <Row className="RowAddWebsite">
        <Col md="6">table</Col>
        <Col md="6 ">
          <input
            label="Name"
            value={name}
            placeholder={`website Name`}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            label="number of visitors"
            value={quantity}
            placeholder={`number of visitors`}
            onChange={(e) => setQuantity(e.target.value)}
          />
          <input
            label="Link"
            value={"www.linkedin.com"}
            placeholder={`website link`}
            onChange={(e) => setQuantity(e.target.value)}
          />
          <input
            label="Price"
            value={price}
            placeholder={`Price`}
            onChange={(e) => setPrice(e.target.value)}
          />
          <input
            label="Description"
            value={description}
            placeholder={`Description`}
            onChange={(e) => setDescription(e.target.value)}
          />
          <select
            className="form-control"
            value={categoryId}
            onChange={(e) => setCategoryId(e.target.value)}
          >
            <option>select category</option>
            {createCategoryList(category.categories).map((option) => (
              <option key={option.value} value={option.value}>
                {option.name}
              </option>
            ))}
          </select>
          {webSitePictures.length > 0
            ? webSitePictures.map((pic, index) => (
                <div key={index}>{pic.name}</div>
              ))
            : null}
          <input
            type="file"
            name="webSitePicture"
            onChange={handlewebSitePictures}
          />
        </Col>
      </Row>
    </Layout>
  );
};

export default Profil;
